mapper.select:

SELECT count(id) 
FROM FBM_USERS
WHERE ID = #{id} 
AND PW = #{pw}

mapper.insert:

INSERT INTO FBM_USER(
	SEQ, ID, PW
	, NAME, NICK_NAME
	, TEAM_ID, CAPTAIN
	, BACK_NO, MAJOR_LEG
	, HEIGHT, WEIGHT
)VALUES(
	FBM_USERS_SEQ.NEXTVAL, #{id}, #{pw}
	, #{name}, #{nick_name}
	, #{team_id}, #{captain}
	, #{back_no}, #{major_leg}
	, #{height}, #{weight}
)
